            });




    function dibujarCarrito(){

        $('.cart-items').html('');

        $.ajax({
                url:"/cart",
                method:'GET',
                dataType:'json',
                success:function(data)
                {
                    console.log(data, 'dibuja');
                    if(data.result){
                        $('.ajax-cart-table-header').show();
                        $('.emptyCart').hide();
                        $('.ajax-cart-total').show();
                        $('.ajax-cart-total-container').show();
                        $('.ajax-cart-bottom').show();
                        var htmlItems = '';
                        for(var i in data.cart.items){
                            htmlItems += addCartItem(data.cart.items[i]);
                        }
                        $('.cart-items').append(htmlItems);
                    }else{
                        // Carrito vacio
                        $('.ajax-cart-table-header').hide();
                        $('.emptyCart').show();
                        $('.ajax-cart-total').hide();
                        $('.ajax-cart-total-container').hide();
                        $('.ajax-cart-bottom').hide();
                    }   
                    $('.cart-total-quantity').html(data.cart.totalQuantity);
                    $('.cart-total-price').html('$'+data.cart.totalPrice);
                    $('.cart-subtotal-price').html('$'+data.cart.totalPrice);
                }
            });
    }


function plusQuantity(id){
    $('.cart-plus-quantity').prop('disabled',true);
    var q = $('#quantity_'+id).val();
    q++;

    $.ajax({
        url:"/cart/add/item",
        method:'GET',
        data:{
            id:id,
            quantity: 1
            },
        dataType:'json',
        success:function(data)
        {
            
            //** Animar algo

            if(data.result){

                dibujarCarrito();

            }else{
                q--;
                alert('La cantidad del modelo seleccionado supera el stock disponible');
            }

            $('#quantity_'+id).val(q);
            $('.cart-plus-quantity').removeProp('disabled');
        }
    });

    
}

function minQuantity(id){

    $('.cart-min-quantity').prop('disabled',true);
    var q = $('#quantity_'+id).val();
    if(q==1) return;
    q--;
    if(q==0){
        removeCartItem(id);
    }

    $.ajax({
        url:"/cart/add/item",
        method:'GET',
        data:{
            id:id,
            quantity: -1
            },
        dataType:'json',
        success:function(data)
        {
            
            //** Animar algo

            if(data.result){

                dibujarCarrito();

            }else{
                q--;
                alert('La cantidad del modelo seleccionado supera el stock disponible');
            }

            $('#quantity_'+id).val(q);
            $('.cart-min-quantity').removeProp('disabled');
        }
    });
}

function removeCartItem(id){

    $('#item_'+id).remove();

    $.ajax({
        url:"/cart/remove",
        method:'GET',
        data:{
            id:id
            },
        dataType:'json',
        success:function(data)
        {
            dibujarCarrito();
        }
    });
}

